DROP TABLE IF EXISTS lib_loan
DROP TABLE IF EXISTS lib_book
DROP TABLE IF EXISTS lib_person

CREATE TABLE lib_person (
	person_id int identity primary key
	, first_name varchar(30) not null
	, last_name varchar(30) not null
)

CREATE TABLE lib_book (
	book_id int identity primary key
	, book_title varchar(75)
)

CREATE TABLE lib_loan (
	loan_id int identity primary key
	, person_id int not null foreign key references lib_person(person_id)
	, book_id int not null foreign key references lib_book(book_id)
	, borrowed_date datetime not null
	, due_date as dateadd(week, 2, borrowed_date)
	, returned_date datetime null
)




